	/**
	 * @return  the plan as a prolog list of moves
	 */
	private String getPrologPlan()
	{
		String moves = "plan([";
		for(int i=0; i<conditionalPlan.size(); i++)
		{
			moves += conditionalPlan.get(i).toString();
			if(i!=conditionalPlan.size()-1)
				moves+=",";
		}
		moves+="])";
		//println(moves);
		return moves;
	}

	
